module.exports = function (grunt) {
  grunt.loadNpmTasks('grunt-mocha-test');

  grunt.registerTask('tests', [
    'mochaTest:unit'
  ]);

  grunt.initConfig({
    mochaTest : {
      options : {
        reporter : 'dot',
        require  : [
          'should'
        ]
      },
      unit : {
        src : [
          'test/unit/**/*.js'
        ]
      }
    }
  });
};